import { Component, Input } from '@angular/core';
import { AlbumResponse } from 'src/app/interfaces/Spotify/Search/AlbumResponse';

@Component({
  selector: 'app-album',
  templateUrl: './album.component.html',
})
export class AlbumComponent {
  @Input() album: AlbumResponse = {} as AlbumResponse;

  AlbumComponent(album: AlbumResponse) {
    this.album = album;
  }

  getArtistName(): string {
    return this.album.artists[0].name;
  }

  addToFavorites(): void {
    console.log('Added to favorites');
  }
}
<div class="relative flex flex-col items-center">
  <div class="w-52 h-52 bg-gray-200 rounded-md">
    <img
      [src]="album.images[0].url"
      [alt]="album.name"
      class="object-cover w-full h-full rounded-md"
    />
    <button
      class="absolute top-0 right-28 p-2 rounded-25 bg-gray-300 hover:bg-green-500 focus:outline-none focus:bg-green-500 transition-colors"
      (click)="addToFavorites()"
    >
      <svg
        xmlns="http://www.w3.org/2000/svg"
        class="h-4 w-4 m-auto"
        fill="none"
        viewBox="0 0 24 24"
        stroke="currentColor"
      >
        <path
          stroke-linecap="round"
          stroke-linejoin="round"
          stroke-width="2"
          d="M12 6v6m0 0v6m0-6h6m-6 0H6"
        />
      </svg>
    </button>
  </div>
  <div class="mt-2 text-black text-center">{{ album.name }}</div>
  <div class="mt-1 text-gray-500 text-center">{{ album.artists[0].name }}</div>
</div>
