import { Component, Input } from '@angular/core';
import { AlbumResponse } from 'src/app/interfaces/Spotify/Search/AlbumResponse';
import { ArtistResponse } from 'src/app/interfaces/Spotify/Search/ArtistResponse';

@Component({
  selector: 'app-artist',
  templateUrl: './artist.component.html',
})
export class ArtistComponent {
  @Input() artist: ArtistResponse = {} as ArtistResponse;

  AlbumComponent(artist: ArtistResponse) {
    this.artist = artist;
  }
}
<div class="relative flex flex-col items-center">
  <div class="w-52 h-52 bg-gray-200 rounded-md">
    <img
      [src]="
        artist.images[0].url != null
          ? artist.images[0].url
          : 'assets/music_placeholder.png'
      "
      [alt]="artist.name"
      class="object-cover w-full h-full rounded-md"
    />
  </div>
  <div class="mt-2 text-black text-center">
    {{ artist.name ? artist.name : "undefined" }}
  </div>
</div>
