import { Component, OnInit } from '@angular/core';
import { FormControl } from '@angular/forms';
import { CollectionService } from '../services/collection.service';
import { AlbumResponse } from '../interfaces/Spotify/Search/AlbumResponse';

@Component({
  selector: 'app-collection',
  templateUrl: './collection.component.html',
})
export class CollectionComponent implements OnInit {
  albums: AlbumResponse[] = [];
  filteredAlbums: AlbumResponse[] = [];
  searchControl = new FormControl('');
  currentPage = 1;
  itemsPerPage = 9;
  collectionId = localStorage.getItem('collectionId');

  constructor(private collectionService: CollectionService) {}

  ngOnInit(): void {
    this.loadCollection();
    this.searchControl.valueChanges.subscribe((searchTerm) => {
      this.filterAlbums(searchTerm!);
    });
  }

  loadCollection(): void {
    this.collectionService
      .getCollection(this.collectionId!)
      .subscribe((collection) => {
        this.albums = collection.albums;
        this.filterAlbums(this.searchControl.value!);
      });
  }

  filterAlbums(searchTerm: string): void {
    const filtered = this.albums.filter((album) =>
      album.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    this.filteredAlbums = filtered;
    this.currentPage = 1;
  }

  get totalPages(): number[] {
    var totalPages: number[] = [];
    const totalAlbums = Math.ceil(this.albums.length / this.itemsPerPage);
    totalPages = Array(totalAlbums)
      .fill(0)
      .map((_, index) => index + 1);
    return totalPages;
  }

  get visibleAlbums(): AlbumResponse[] {
    const startIndex = (this.currentPage - 1) * this.itemsPerPage;
    return this.filteredAlbums.slice(
      startIndex,
      startIndex + this.itemsPerPage
    );
  }

  setCurrentPage(page: number): void {
    if (page >= 1 && page <= this.totalPages.length) {
      this.currentPage = page;
    }
  }

  getIterable(value: any): any[] {
    return Array.isArray(value) ? value : Array.from({ length: value });
  }
}
<div class="container mx-auto">
  <div class="mb-4">
    <input
      type="text"
      class="w-full px-4 py-2 border rounded-md"
      placeholder="Search albums"
      [formControl]="searchControl"
    />

    <button
      (click)="searchAlbum()"
      class="px-4 py-2 bg-gray-900 text-white rounded-r-md hover:bg-gray-700 focus:outline-none"
    >
      <svg
        xmlns="http://www.w3.org/2000/svg"
        class="h-6 w-6"
        fill="none"
        viewBox="0 0 20 20"
        stroke="#fff"
      >
        <path
          stroke-linecap="round"
          stroke-linejoin="round"
          stroke-width="1.5"
          d="M17.545 15.467l-3.779-3.779a6.15 6.15 0 0 0 .898-3.21c0-3.417-2.961-6.377-6.378-6.377A6.185 6.185 0 0 0 2.1 8.287c0 3.416 2.961 6.377 6.377 6.377a6.15 6.15 0 0 0 3.115-.844l3.799 3.801a.953.953 0 0 0 1.346 0l.943-.943c.371-.371.236-.84-.135-1.211zM4.004 8.287a4.282 4.282 0 0 1 4.282-4.283c2.366 0 4.474 2.107 4.474 4.474a4.284 4.284 0 0 1-4.283 4.283c-2.366-.001-4.473-2.109-4.473-4.474z"
        />
      </svg>
    </button>
  </div>
  <div class="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
    <div class="flex flex-col" *ngFor="let album of visibleAlbums"></div>
  </div>
  <div class="mt-4">
    <ul class="flex justify-center space-x-2">
      <li>
        <button
          class="btn-pagination"
          [class.disabled]="currentPage === 1"
          (click)="setCurrentPage(1)"
        >
          First
        </button>
      </li>
      <li>
        <button
          class="btn-pagination"
          [class.disabled]="currentPage === 1"
          (click)="setCurrentPage(currentPage - 1)"
        >
          Previous
        </button>
      </li>
      <li
        *ngFor="let page of getIterable(totalPages)"
        [class.active]="currentPage === page"
      >
        <button class="btn-pagination" (click)="setCurrentPage(page)">
          {{ page }}
        </button>
      </li>
      <li>
        <button
          class="btn-pagination"
          [class.disabled]="currentPage === totalPages.length"
          (click)="setCurrentPage(currentPage + 1)"
        >
          Next
        </button>
      </li>
      <li>
        <button
          class="btn-pagination"
          [class.disabled]="currentPage === totalPages.length"
          (click)="setCurrentPage(totalPages.length)"
        >
          Last
        </button>
      </li>
    </ul>
  </div>
</div>
