      this.artistSearchResults = [];
      this.albumSearchResults = [];
      this.currentPage = 1;
      try {
        this.spotifyService.searchArtist(this.searchQuery).subscribe({
          error: (response) => {
            console.log('Search failed: ' + response.message);
          },
          next: (response: any) => {
            this.artistSearchResults = response.artists.items;
          },
        });
      } catch (error) {
        console.log(error);
      }
    }
