import { Component, Input, ChangeDetectionStrategy } from '@angular/core';
import { AlbumComplete } from 'src/app/interfaces/Spotify/Search/AlbumComplete';
import { CollectionService } from 'src/app/services/collection.service';

@Component({
  selector: 'app-full-album',
  templateUrl: './full-album.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class FullAlbumComponent {
  @Input() album: AlbumComplete = {} as AlbumComplete;

  constructor(private collectionService: CollectionService) {}

  getArtistName(): string {
    return this.album.artists[0].name || '';
  }

  getSpotifyLink(): string {
    if (this.album.external_urls.has('spotify')) {
      return this.album.external_urls.get('spotify');
    }
  }

  removeFromFavorites(): void {
    const collectionId = localStorage.getItem('collectionId');
    if (collectionId !== null) {
      this.collectionService
        .removeAlbumFromCollection(collectionId, this.album)
        .subscribe(() => {
          this.album.added_to_collection = false;
        });
    }
  }
}
<div class="relative flex flex-col items-center">
  <a [href]="getSpotifyLink()" target="_blank">
    <div class="w-52 h-52 bg-gray-200 rounded-md">
      <img
        [src]="album.images[0].url || 'assets/music_placeholder.png'"
        [alt]="album.name"
        class="object-cover w-full h-full rounded-md"
      />
      <button
        class="absolute top-0 right-0 p-2 rounded-25 bg-green-500 hover:bg-red-500 focus:outline-none transition-colors"
        (click)="removeFromFavorites()"
      >
        <svg
          xmlns="http://www.w3.org/2000/svg"
          class="h-4 w-4 m-auto"
          fill="none"
          viewBox="0 0 16 16"
          stroke="currentColor"
        >
          <path
            stroke-linecap="round"
            stroke-linejoin="round"
            stroke-width="2"
            d="M4.646 4.646a.5.5 0 0 1 .708 0L8 7.293l2.646-2.647a.5.5 0 0 1 .708.708L8.707 8l2.647 2.646a.5.5 0 0 1-.708.708L8 8.707l-2.646 2.647a.5.5 0 0 1-.708-.708L7.293 8 4.646 5.354a.5.5 0 0 1 0-.708z"
          />
        </svg>
      </button>
    </div>
  </a>
  <div class="mt-2 text-black text-center">
    {{ album.name || "undefined" }}
  </div>
  <div class="mt-1 text-gray-500 text-center">{{ getArtistName() }}</div>
  <div class="mt-1 text-gray-500 text-center">{{ album.release_date }}</div>
  <div class="mt-1 text-gray-500 text-center">
    {{ album.total_tracks }} tracks
  </div>
</div>
