import { Component, OnInit } from '@angular/core';
import { FormControl } from '@angular/forms';
import { CollectionService } from '../services/collection.service';
import { AlbumResponse } from '../interfaces/Spotify/Search/AlbumResponse';

@Component({
  selector: 'app-collection',
  templateUrl: './collection.component.html',
  styleUrls: ['./collection.component.css'],
})
export class CollectionGridComponent implements OnInit {
  albums: AlbumResponse[] = [];
  filteredAlbums: AlbumResponse[] = [];
  searchControl = new FormControl('');
  currentPage = 1;
  itemsPerPage = 9;
  collectionId = localStorage.getItem('collectionId');

  constructor(private collectionService: CollectionService) {}

  ngOnInit(): void {
    this.loadCollection();
    this.searchControl.valueChanges.subscribe((searchTerm) => {
      this.filterAlbums(searchTerm!);
    });
  }

  loadCollection(): void {
    this.collectionService
      .getCollection(this.collectionId!)
      .subscribe((collection) => {
        this.albums = collection.albums;
        this.filterAlbums(this.searchControl.value!);
      });
  }

  filterAlbums(searchTerm: string): void {
    const filtered = this.albums.filter((album) =>
      album.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    this.filteredAlbums = filtered;
    this.currentPage = 1;
  }

  get totalPages(): number {
    return Math.ceil(this.filteredAlbums.length / this.itemsPerPage);
  }

  get visibleAlbums(): AlbumResponse[] {
    const startIndex = (this.currentPage - 1) * this.itemsPerPage;
    return this.filteredAlbums.slice(
      startIndex,
      startIndex + this.itemsPerPage
    );
  }

  onPageChange(page: number): void {
    this.currentPage = page;
  }
}
<div class="container mx-auto">
  <div class="mb-4">
    <input
      type="text"
      class="w-full px-4 py-2 border rounded-md"
      placeholder="Search albums"
      [formControl]="searchControl"
    />
  </div>
  <div class="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
    <div class="flex flex-col" *ngFor="let album of visibleAlbums"></div>
  </div>
  <div class="mt-4">
    <nav class="flex items-center justify-center" aria-label="Page navigation">
      <ul class="pagination">
        <li class="page-item" [class.disabled]="currentPage === 1">
          <a class="page-link" href="#" (click)="onPageChange(currentPage - 1)">
            Previous
          </a>
        </li>
        <li
          class="page-item"
          *ngFor="let page of totalPages"
          [class.active]="currentPage === page"
        >
          <a class="page-link" href="#" (click)="onPageChange(page)">
            {{ page }}
          </a>
        </li>
        <li class="page-item" [class.disabled]="currentPage === totalPages">
          <a class="page-link" href="#" (click)="onPageChange(currentPage + 1)">
            Next
          </a>
        </li>
      </ul>
    </nav>
  </div>
</div>
