import { Inject, Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, of } from 'rxjs';
import { switchMap } from 'rxjs/operators';
import { RegisterRequest } from '../interfaces/RegisterRequest';
import { RegisterResponse } from '../interfaces/RegisterResponse';
import { LoginRequest } from '../interfaces/LoginRequest';
import { LoginResponse } from '../interfaces/LoginResponse';
import jwt_decode from 'jwt-decode';

@Injectable({
  providedIn: 'root',
})
export class AuthService {
  private isAuthenticated: boolean = false;

  constructor(
    private http: HttpClient,
    @Inject('BASE_URL') private baseUrl: string
  ) {
    this.isAuthenticated = this.checkTokenValidity();
  }

  isLoggedIn(): boolean {
    return this.isAuthenticated;
  }

  cleanStorage(): void {
    localStorage.removeItem('jwt');
    localStorage.removeItem('username');
    localStorage.removeItem('userId');
  }

  register(registerData: RegisterRequest): Observable<RegisterResponse> {
    this.cleanStorage();
    return this.registerAsync(registerData).pipe(
      switchMap((response: RegisterResponse) => {
        return of(response);
      })
    );
  }

  registerAsync(registerData: RegisterRequest): Observable<RegisterResponse> {
    return this.http.post<RegisterResponse>(
      this.baseUrl + 'api/v1/authenticate/register',
      JSON.stringify(registerData),
      { headers: { 'Content-Type': 'application/json' } }
    );
  }

  login(loginData: LoginRequest): Observable<LoginResponse> {
    this.cleanStorage();
    return this.loginAsync(loginData).pipe(
      switchMap((response: LoginResponse) => {
        return of(response);
      })
    );
  }

  loginAsync(loginData: LoginRequest): Observable<LoginResponse> {
    return this.http.post<LoginResponse>(
      this.baseUrl + 'api/v1/authenticate/login',
      JSON.stringify(loginData),
      {
        headers: { 'Content-Type': 'application/json' },
      }
    );
  }

  logout() {
    this.cleanStorage();
    this.isAuthenticated = false;
  }

  private checkTokenValidity(): boolean {
    const token = localStorage.getItem('jwt'); // Update token key to match the one you're using

    if (token) {
      // Perform additional checks if needed, such as token expiration or validity
      // Example:
      const decodedToken = this.decodeToken(token);
      const expirationDate = new Date(decodedToken.exp * 1000); // Convert expiration time to milliseconds
      const currentDate = new Date();
      if (expirationDate > currentDate) {
        return true;
      }
    }

    return false;
  }
}
