import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { tap } from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  private isAuthenticated: boolean = false;

  constructor(private http: HttpClient) {
    this.isAuthenticated = this.checkTokenValidity();
  }

  isLoggedIn(): boolean {
    return this.isAuthenticated;
  }

  async register(registerData: any): Promise<Observable<any>> {
    return this.http.post<any>('/api/v1/authenticate/register', registerData);
  }

  async login(loginData: any): Promise<Observable<any>> {
    return this.http.post<any>('/api/v1/authenticate/login', loginData).pipe(
      tap(response => {
        if (response && response.success) {
          // Set the JWT token in local storage or any other storage mechanism
          localStorage.setItem('token', response.accessToken);
          // Update the authentication status
          this.isAuthenticated = true;
        }
      })
    );
  }

  logout() {
    localStorage.removeItem('token');
    this.isAuthenticated = false;
  }

  private checkTokenValidity(): boolean {
    const token = localStorage.getItem('token');

    if (token) {
      return true;
    }

    return false;
  }
}
