import { Component } from '@angular/core';
import { SpotifyService } from '../services/spotify.service';
import { AlbumResponse } from '../interfaces/Spotify/Search/AlbumResponse';
import { AlbumList } from '../interfaces/Spotify/Search/AlbumList';

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
})
export class SearchComponent {
  searchQuery: string = '';
  searchType: string = 'artist';

  constructor(private spotifyService: SpotifyService) {}

  search(): void {
    switch (this.searchType) {
      case 'artist':
        this.searchArtist();
        break;
      case 'album':
        this.searchAlbum();
        break;
      default:
        break;
    }
  }
  searchArtist() {
    try {
      this.spotifyService.searchAlbum(this.searchQuery).subscribe({});
    } catch (error) {
      console.log(error);
    }
  }

  searchAlbum(): void {
    try {
      this.spotifyService.searchAlbum(this.searchQuery).subscribe({
        error: (response) => {
          console.log('Search failed: ' + response.message);
        },
        next: (response: any) => {
          console.log(response.albums);
        },
      });
    } catch (error) {
      console.log(error);
    }
  }
}
<div class="container mx-auto p-4">
  <div class="flex items-center">
    <input
      [(ngModel)]="searchQuery"
      type="text"
      placeholder="Search..."
      class="w-full px-4 py-2 rounded-l-md border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500"
    />
    <select
      [(ngModel)]="searchType"
      class="px-4 py-2 bg-gray-100 border border-gray-300 rounded-none focus:outline-none"
    >
      <option value="artist">Artist</option>
      <option value="album">Album</option>
    </select>
    <button
      (click)="search()"
      class="px-4 py-2 bg-gray-900 text-white rounded-r-md hover:bg-gray-700 focus:outline-none"
    >
      <svg
        xmlns="http://www.w3.org/2000/svg"
        class="h-6 w-6"
        fill="none"
        viewBox="0 0 20 20"
        stroke="#fff"
      >
        <path
