import { Inject, Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { tap } from 'rxjs/operators';
import { RegisterRequest } from '../interfaces/RegisterRequest';
import { RegisterResponse } from '../interfaces/RegisterResponse';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  private isAuthenticated: boolean = false;

  constructor(private http: HttpClient, @Inject('BASE_URL') private baseUrl: string) {
    this.isAuthenticated = this.checkTokenValidity();
  }

  isLoggedIn(): boolean {
    return this.isAuthenticated;
  }

  register(registerData: RegisterRequest): Observable<RegisterResponse> {
    return this.http.post<RegisterResponse>(this.baseUrl + 'api/v1/authenticate/register', JSON.stringify(registerData), { headers: { 'Content-Type': 'application/json' } }).pipe(tap(response => {
      if (response && response.Success) {
        console.log(response.Message);
        this.isAuthenticated = true;
      } else {
        console.log('Registration failed');
      }
    }));
  }

  login(loginData: any): Observable<any> {
    return this.http.post<any>('/api/v1/authenticate/login', loginData, { headers: { 'Content-Type': 'application/json' } }).pipe(
      tap(response => {
        if (response && response.success) {
          localStorage.setItem('accessToken', response.accessToken);
          localStorage.setItem('username', response.username);
          localStorage.setItem('userId', response.userId);
          this.isAuthenticated = true;
        }
      })
    );
  }

  logout() {
    localStorage.removeItem('accessToken');
    localStorage.removeItem('username');
    localStorage.removeItem('userId');
    this.isAuthenticated = false;
  }

  private checkTokenValidity(): boolean {
    const token = localStorage.getItem('token');

    if (token) {
      return true;
    }

    return false;
  }
}
